library SaturatingSub {
  /// @notice Do a - b but if that would result in underflow error, then just return 0
  function saturatingSub(uint256 a, uint256 b) internal pure returns (uint256) {
    unchecked {
      return b > a ? 0 : a - b;
    }
  }
}

