// SPDX-License-Identifier: MIT

pragma solidity ^0.8.16;

import "@openzeppelin/contracts-upgradeable/interfaces/IERC20Upgradeable.sol";
// solhint-disable-next-line max-line-length
import {SafeERC20Upgradeable as SafeERC20} from "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";

import "../../../../library/FiduConversions.sol";
import {Context} from "../../../../cake/Context.sol";
import "../../../../cake/Routing.sol" as Routing;

import {CapitalAssetType} from "../../../../interfaces/ICapitalLedger.sol";
import {IStakingRewards, StakedPositionType} from "../../../../interfaces/IStakingRewards.sol";
import {ISeniorPool} from "../../../../interfaces/ISeniorPool.sol";

using Routing.Context for Context;
using SafeERC20 for IERC20Upgradeable;

library StakedFiduAsset {
  CapitalAssetType public constant AssetType = CapitalAssetType.ERC721;

  /**
   * @notice Get the type of asset that this contract adapts.
   * @return the asset type
   */
  function isType(Context context, address assetAddress) internal view returns (bool) {
    return assetAddress == address(context.stakingRewards());
  }

  /**
   * @notice Get whether or not the given asset is valid
   * @return true if the asset is Fidu type (not CurveLP)
   */
  function isValid(Context context, uint256 assetTokenId) internal view returns (bool) {
    return
      context.stakingRewards().getPosition(assetTokenId).positionType == StakedPositionType.Fidu;
  }

  /**
   * @notice Get the point-in-time USDC equivalent value of the ERC721 asset. This
   *  specifically attempts to return the "principle" or "at-risk" USDC value of
   *  the asset and does not include rewards, interest, or other benefits.
   * @param context goldfinch context for routing
   * @param assetTokenId id of the position to evaluate
   * @return USDC equivalent value
   */
  function getUsdcEquivalent(
    Context context,
    uint256 assetTokenId
  ) internal view returns (uint256) {
    uint256 stakedFiduBalance = context.stakingRewards().stakedBalanceOf(assetTokenId);
    return FiduConversions.fiduToUsdc(stakedFiduBalance, context.seniorPool().sharePrice());
  }

  /**
   * @notice Harvest GFI rewards on a staked fidu token and send them to `owner`.
   * @param context goldfinch context for routing
   * @param owner address to send the GFI to
   * @param assetTokenId id of the position to harvest
   */
  function harvest(Context context, address owner, uint256 assetTokenId) internal {
    // Sends reward to owner (this contract)
    uint256 reward = context.stakingRewards().getReward(assetTokenId);

    if (reward > 0) {
      context.gfi().safeTransfer(owner, reward);
    }
  }
}

contracts/protocol/core/membership/Epoch.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.16;

library Epochs {
  uint256 internal constant EPOCH_SECONDS = 7 days;

  /**
   * @notice Get the epoch containing the timestamp `s`
   * @param s the timestamp
   * @return corresponding epoch
   */
  function fromSeconds(uint256 s) internal pure returns (uint256) {
    return s / EPOCH_SECONDS;
  }

  /**
   * @notice Get the current epoch for the block.timestamp
   * @return current epoch
   */
  function current() internal view returns (uint256) {
    return fromSeconds(block.timestamp);
  }

  /**
   * @notice Get the start timestamp for the current epoch
   * @return current epoch start timestamp
   */
  function currentEpochStartTimestamp() internal view returns (uint256) {
    return startOf(current());
  }

  /**
   * @notice Get the previous epoch given block.timestamp
   * @return previous epoch
   */
  function previous() internal view returns (uint256) {
    return current() - 1;
  }

  /**
   * @notice Get the next epoch given block.timestamp
   * @return next epoch
   */
  function next() internal view returns (uint256) {
    return current() + 1;
  }

  /**
   * @notice Get the Unix timestamp of the start of `epoch`
   * @param epoch the epoch
   * @return unix timestamp
   */
  function startOf(uint256 epoch) internal pure returns (uint256) {
    return epoch * EPOCH_SECONDS;
  }
}

contracts/protocol/core/membership/ERC20.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.16;

import "openzeppelin-contracts-0-8-x/token/ERC20/IERC20.sol";
import "openzeppelin-contracts-0-8-x/token/ERC20/utils/SafeERC20.sol";
import "openzeppelin-contracts-0-8-x/utils/Address.sol";

import {Context} from "../../../cake/Context.sol";
import {Base} from "../../../cake/Base.sol";
import {PausableUpgradeable} from "../../../cake/Pausable.sol";
import {IERC20Splitter} from "../../../interfaces/IERC20Splitter.sol";

using Address for address;
using SafeERC20 for IERC20;

interface IERC20SplitterReceiver {
  function onReceive(uint256 amount) external returns (bytes4 retval);
}

/// @title ERC20Splitter
/// @author landakram
/// @notice Splits the ERC20 balance of this contract amongst a list of payees.
///   Unlike similar splitter contracts, all shares of the balance are distributed
///   in a single `distribute` transaction. If a payee is a smart contract implementing
///   `IERC20SplitterReceiver`, then its `onReceive` handler function will be called
///   after it receives its share.
contract ERC20Splitter is IERC20Splitter, Base, PausableUpgradeable {
  error LengthMismatch();
  error InvalidReceiver();
  error IntraBlockDistribution();

  event Distributed(uint256 total);
  event PayeeAdded(address indexed payee, uint256 share);

  /// @notice The total number of shares in the splitter. A payee's proportion
  ///   of the split can be calculated as its share / totalShares.
  uint256 public totalShares;

  /// @notice A list of payees
  address[] public payees;

  /// @notice Payee shares
  mapping(address => uint256) public shares;

  /// @notice The ERC20 that is distributed to payees
  IERC20 public immutable erc20;

  /// @notice The block.timestamp when `distribute` was last called
  uint256 public lastDistributionAt;

  constructor(Context _context, IERC20 _erc20) Base(_context) {
    erc20 = _erc20;
  }

  function initialize() external initializer {
    __Pausable_init_unchained();
  }

  function pendingDistributionFor(address payee) external view returns (uint256) {
    return (erc20.balanceOf(address(this)) * shares[payee]) / totalShares;
  }

  /// @notice Distribute the current balance to payees. If a payee is a smart contract
  ///   implementing `IERC20SplitterReceiver`, then its `onReceive` handler function will
  ///   be called after it receives its share.
  function distribute() external whenNotPaused {
    if (lastDistributionAt == block.timestamp) revert IntraBlockDistribution();

    lastDistributionAt = block.timestamp;

    uint256 totalToDistribute = erc20.balanceOf(address(this));

    for (uint256 i = 0; i < payees.length; i++) {
      address payee = payees[i];
      uint256 share = shares[payee];

      // Due to integer division, this could result in some dust being left over in the
      // contract. This is acceptable as the dust will be included in the next distribution.
      uint256 owedToPayee = (totalToDistribute * share) / totalShares;
      if (owedToPayee > 0) {
        erc20.safeTransfer(payee, owedToPayee);
      }

      if (payee.isContract()) {
        // Call this even if there is nothing owed to payee. Some recipients may still need
        // to account for the event.
        triggerOnReceive(payee, owedToPayee);
      }
    }

    emit Distributed(totalToDistribute);
  }

  function triggerOnReceive(address payee, uint256 amount) internal {
    try IERC20SplitterReceiver(payee).onReceive(amount) returns (bytes4 retval) {
      if (retval != IERC20SplitterReceiver.onReceive.selector) revert InvalidReceiver();
    } catch (bytes memory reason) {
      // A zero-length reason means the payee does not implement IERC20SplitterReceiver.
      // In that case, just continue.
      if (reason.length > 0) {
        assembly {
          revert(add(32, reason), mload(reason))
        }
      }
    }
  }

  /// @notice Replace all current payees with a new set of payees and shares
  /// @param _payees An array of addresses to receive distributions
  /// @param _shares An array of shares (ordered by `_payees`) to use for distributions
  function replacePayees(
    address[] calldata _payees,
    uint256[] calldata _shares
  ) external onlyAdmin {
    delete payees;
    _setUpPayees(_payees, _shares);
  }

  function _setUpPayees(address[] calldata _payees, uint256[] calldata _shares) internal {
    if (_payees.length != _shares.length) revert LengthMismatch();

    totalShares = 0;
    payees = _payees;

    for (uint256 i = 0; i < _shares.length; i++) {
      address payee = _payees[i];
      uint256 share = _shares[i];
      shares[payee] = share;
      totalShares += share;
      emit PayeeAdded({payee: payee, share: share});
    }
  }
}

contracts/protocol/core/membership/GFILe.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.16;

import "@openzeppelin/contracts-upgradeable/interfaces/IERC20Upgradeable.sol";
// solhint-disable-next-line max-line-length
import {SafeERC20Upgradeable as SafeERC20} from "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";

import {Context} from "../../../cake/Context.sol";
import {Base} from "../../../cake/Base.sol";
import "../../../cake/Routing.sol" as Routing;

import {Arrays} from "../../../library/Arrays.sol";
import {UserEpochTotals, UserEpochTotal} from "./UserEpochTotals.sol";

import "../../../interfaces/IGFILedger.sol";

using Routing.Context for Context;
using UserEpochTotals for UserEpochTotal;
using Arrays for uint256[];
using SafeERC20 for IERC20Upgradeable;

/**
 * @title GFILedger
 * @notice Track GFI held by owners and ensure the GFI has been accounted for.
 * @author Goldfinch
 */
contract GFILedger is IGFILedger, Base {
  /// Thrown when depositing zero GFI for a position
  error ZeroDepositAmount();
  /// Thrown when withdrawing an invalid amount for a position
  error InvalidWithdrawAmount(uint256 requested, uint256 max);
  /// Thrown when depositing from address(0)
  error InvalidOwnerIndex();
  /// Thrown when querying token supply with an index greater than the supply
  error IndexGreaterThanTokenSupply();

  // All positions in the ledger
  mapping(uint256 => Position) public positions;

  // Which positions an address owns
  mapping(address => uint256[]) private owners;

  /// Total held by each user, while being aware of the deposit epoch
  mapping(address => UserEpochTotal) private totals;

  // Most recent position minted
  uint256 private positionCounter;

  /// @notice Construct the contract
  constructor(Context _context) Base(_context) {}

  /// @inheritdoc IGFILedger
  function deposit(
    address owner,
    uint256 amount
  ) external onlyOperator(Routing.Keys.MembershipOrchestrator) returns (uint256 positionId) {
    if (amount == 0) {
      revert ZeroDepositAmount();
    }
    positionId = _mintPosition(owner, amount);

    totals[owner].recordIncrease(amount);

    context.gfi().safeTransferFrom(
      address(context.membershipOrchestrator()),
      address(this),
      amount
    );
  }

  /// @inheritdoc IGFILedger
  function withdraw(
    uint256 positionId
  ) external onlyOperator(Routing.Keys.MembershipOrchestrator) returns (uint256) {
    return _withdraw(positionId);
  }

  /// @inheritdoc IGFILedger
  function withdraw(
    uint256 positionId,
    uint256 amount
  ) external onlyOperator(Routing.Keys.MembershipOrchestrator) returns (uint256) {
    Position memory position = positions[positionId];

    if (amount > position.amount) revert InvalidWithdrawAmount(amount, position.amount);
    if (amount == position.amount) return _withdraw(positionId);

    positions[positionId].amount -= amount;
    totals[position.owner].recordDecrease(amount, position.depositTimestamp);

    context.gfi().safeTransfer(position.owner, amount);

    emit GFIWithdrawal({
      owner: position.owner,
      positionId: positionId,
      withdrawnAmount: amount,
      remainingAmount: position.amount - amount,
      depositTimestamp: position.depositTimestamp
    });

    return amount;
  }

  /// @inheritdoc IGFILedger
  function balanceOf(address addr) external view returns (uint256 balance) {
    return owners[addr].length;
  }

  /// @inheritdoc IGFILedger
  function ownerOf(uint256 positionId) external view returns (address) {
    return positions[positionId].owner;
  }

  /// @inheritdoc IGFILedger
  function totalsOf(
    address addr
  ) external view returns (uint256 eligibleAmount, uint256 totalAmount) {
    return totals[addr].getTotals();
  }

  /// @inheritdoc IGFILedger
  function totalSupply() public view returns (uint256) {
    return positionCounter;
  }

  /// @inheritdoc IGFILedger
  function tokenOfOwnerByIndex(address owner, uint256 index) external view returns (uint256) {
    if (index >= owners[owner].length) revert InvalidOwnerIndex();

    return owners[owner][index];
  }

  /// @inheritdoc IGFILedger
  function tokenByIndex(uint256 index) external view returns (uint256) {
    if (index >= totalSupply()) revert IndexGreaterThanTokenSupply();

    return index + 1;
  }

  //////////////////////////////////////////////////////////////////
  // Private

  function _mintPosition(address owner, uint256 amount) private returns (uint256 positionId) {
    positionCounter++;

    positionId = positionCounter;

    positions[positionId] = Position({
      owner: owner,
      ownedIndex: owners[owner].length,
      amount: amount,
      depositTimestamp: block.timestamp
    });

    owners[owner].push(positionId);

    emit GFIDeposit({owner: owner, positionId: positionId, amount: amount});
  }

  function _withdraw(uint256 positionId) private returns (uint256) {
    Position memory position = positions[positionId];
    delete positions[positionId];

    uint256[] storage ownersList = owners[position.owner];
    (, bool replaced) = ownersList.reorderingRemove(position.ownedIndex);
    if (replaced) {
      positions[ownersList[position.ownedIndex]].ownedIndex = position.ownedIndex;
    }

    totals[position.owner].recordDecrease(position.amount, position.depositTimestamp);

    context.gfi().safeTransfer(position.owner, position.amount);

    emit GFIWithdrawal({
      owner: position.owner,
      positionId: positionId,
      withdrawnAmount: position.amount,
      remainingAmount: 0,
      depositTimestamp: position.depositTimestamp
    });

    return position.amount;
  }
}

